and a.store_id in
#{item}

select a.* from ( select @ROWNUM:=@ROWNUM+1 AS rownum, a.store_id, a.high_store_id, a.passwd, a.business_nm, a.business_nm3, a.ceo, a.ceo_birth, a.corp_regist_num, a.corp_regist_num2, a.corp_type, a.business_cond, a.industry_type, a.tel, a.fax, a.phone_num, a.email, a.contract_date, a.bank_code, a.account_num, a.state, a.hompage, a.zip_code, a.address, a.detail_address, a.terminal_id, replace(a.person_nm1, substr(a.person_nm1, 2, 1), '*') person_nm1, concat(substring(a.person_phone1, 1, 9), '****') as person_phone1, concat(substring(a.person_email1, 1, length(substring_index(a.person_email1, '@', 1))-3), '***@', substring_index(a.person_email1, '@', -1)) as person_email1, a.person_nm2, a.person_phone2, a.person_email2, a.person_nm3, a.person_phone3, a.person_email3, a.commission, a.itg_limitset, a.period, a.tax, a.deposit, a.created_id, a.created_datetime, a.updated_id, a.updated_datetime, a.credit_card_k, a.credit_card, a.terminal, a.virtual_account, a.gift_voucher, a.smart_gift_voucher, a.book_gift_voucher, a.account_transfer from ( select a.store_id, a.high_store_id, a.passwd, a.business_nm, c.business_nm as business_nm3, a.ceo, a.ceo_birth, a.corp_regist_num, a.corp_regist_num2, a.corp_type, a.business_cond, a.industry_type, a.tel, a.fax, a.phone_num, a.email, a.contract_date, a.bank_code, a.account_num, a.state, a.hompage, a.zip_code, a.address, a.detail_address, a.terminal_id, a.person_nm1, a.person_phone1, a.person_email1, a.person_nm2, a.person_phone2, a.person_email2, a.person_nm3, a.person_phone3, a.person_email3, a.commission, a.itg_limitset, a.period, a.tax, a.deposit, a.created_id, a.created_datetime, a.updated_id, a.updated_datetime, d.credit_card_k, d.credit_card, d.terminal, d.virtual_account, d.gift_voucher, d.smart_gift_voucher, d.book_gift_voucher, d.account_transfer from ( select a.store_id, a.high_store_id, a.passwd, a.business_nm, a.ceo, a.ceo_birth, a.corp_regist_num, a.corp_regist_num2, a.corp_type, a.business_cond, a.industry_type, a.tel, a.fax, a.phone_num, a.email, a.contract_date, a.bank_code, a.account_num, a.state, a.hompage, a.zip_code, a.address, a.detail_address, a.terminal_id, a.person_nm1, a.person_phone1, a.person_email1, a.person_nm2, a.person_phone2, a.person_email2, a.person_nm3, a.person_phone3, a.person_email3, a.commission, a.itg_limitset, a.period, a.tax, a.deposit, a.created_id, date_format(a.created_datetime, '%Y.%m.%d') created_datetime, a.updated_id, a.updated_datetime from tb_store a inner join tb_store_role b on a.store_id = b.store_id and b.role_id = '1003'
and a.high_store_id = #{high_store_id}
and a.store_id = #{roleStore}
and a.store_id = #{store_id}
and date(a.created_datetime) BETWEEN #{start_datetime} AND #{end_datetime}
and a.business_nm like concat('%', #{business_nm}, '%')
and a.tel like concat('%', #{tel}, '%')
and concat(a.email, a.person_email1, a.person_email2, a.person_email3) like concat('%' , #{email} , '%')
and a.email like concat('%' , #{email} , '%')
and a.person_email1 like concat('%' , #{email} , '%')
and a.person_email2 like concat('%' , #{email} , '%')
and a.person_email3 like concat('%' , #{email} , '%')
and concat(a.phone_num, a.person_phone1, a.person_phone2, a.person_phone3) like concat('%' , #{phone_num} , '%')
and a.phone_num like concat('%' , #{phone_num} , '%')
and a.person_phone1 like concat('%' , #{phone_num} , '%')
and a.person_phone2 like concat('%' , #{phone_num} , '%')
and a.person_phone3 like concat('%' , #{phone_num} , '%')
and a.ceo like concat('%' , #{person_nm} , '%')
and a.person_nm1 like concat('%' , #{person_nm} , '%')
and a.terminal_id like concat('%', #{terminal_id}, '%')
and a.state = #{state}
)a inner join ( select a.store_id, a.high_store_id, a.business_nm from tb_store a inner join tb_store_role b on a.store_id = b.store_id and b.role_id = '1002' )c on a.high_store_id = c.store_id left join tb_store_commission d on d.store_id = a.store_id and d.active = 1 order by case when #{order_no} = '1' then ( case #{order_id} when 'store_id' then a.store_id when 'business_nm' then a.business_nm when 'created_datetime' then a.created_datetime when 'corp_regist_num' then a.corp_regist_num when 'terminal_id' then a.terminal_id when 'tel' then a.tel when 'person_nm1' then a.person_nm1 when 'person_email1' then a.person_email1 when 'person_phone1' then a.person_phone1 when 'business_nm3' then c.business_nm when 'state' then a.state when 'tax' then a.tax end )END ASC, case when #{order_no} = '2' then ( case #{order_id} when 'store_id' then a.store_id when 'business_nm' then a.business_nm when 'created_datetime' then a.created_datetime when 'corp_regist_num' then a.corp_regist_num when 'terminal_id' then a.terminal_id when 'tel' then a.tel when 'person_nm1' then a.person_nm1 when 'person_email1' then a.person_email1 when 'business_nm3' then c.business_nm when 'person_phone1' then a.person_phone1 when 'state' then a.state when 'tax' then a.tax end )END DESC )a where (SELECT @ROWNUM:=0)=0 )a order by a.created_datetime desc LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

select a.store_id, a.high_store_id, a.passwd, a.business_nm, c.business_nm as business_nm3, a.ceo, a.ceo_birth, a.corp_regist_num, a.corp_regist_num2, a.corp_type, a.business_cond, a.industry_type, a.tel, a.fax, a.phone_num, a.email, a.contract_date, a.bank_code, a.account_num, a.state, a.hompage, a.zip_code, a.address, a.detail_address, a.terminal_id, a.person_nm1, a.person_phone1, a.person_email1, a.person_nm2, a.person_phone2, a.person_email2, a.person_nm3, a.person_phone3, a.person_email3, a.commission, a.period, a.tax, a.created_id, a.created_datetime, a.updated_id, a.updated_datetime from ( select a.store_id, a.high_store_id, a.passwd, a.business_nm, a.ceo, a.ceo_birth, a.corp_regist_num, a.corp_regist_num2, a.corp_type, a.business_cond, a.industry_type, a.tel, a.fax, a.phone_num, a.email, a.contract_date, a.bank_code, a.account_num, a.state, a.hompage, a.zip_code, a.address, a.detail_address, a.terminal_id, a.person_nm1, a.person_phone1, a.person_email1, a.person_nm2, a.person_phone2, a.person_email2, a.person_nm3, a.person_phone3, a.person_email3, a.commission, a.period, a.tax, a.created_id, date_format(a.created_datetime, '%Y.%m.%d') created_datetime, a.updated_id, a.updated_datetime from tb_store a inner join tb_store_role b on a.store_id = b.store_id and b.role_id = '1003'
and a.store_id in
#{item}
)a inner join ( select a.store_id, a.high_store_id, a.business_nm from tb_store a inner join tb_store_role b on a.store_id = b.store_id and b.role_id = '1002' )c on a.high_store_id = c.store_id

