mapper.select:
  	   	select concat(no) file_num,
  	   		   file_no,  
  	   		   file_nm
	   	  from tb_file
	   	 where file_type = #{file_type}
	   	   and file_check = #{file_check}
	   	   and file_no = #{no}

        select path, no 
	 	  from tb_file 
		 where file_no = #{file_no}
	       and file_type = #{file_type}

select @ROWNUM:=@ROWNUM+1 AS rownum, a.no, a.store_id, a.role_id, a.cnt, a.title, a.tel, a.email, b.ceo, date_format(a.created_datetime, '%Y.%m.%d') created_datetime from tb_inquiry a inner join tb_store b on a.store_id = b.store_id where (SELECT @ROWNUM:=0)=0 and a.role_id = #{role_id}
and a.store_id = #{store_id}
and (a.title like concat('%',#{searchKeyword},'%') or a.content like concat('%',#{searchKeyword},'%') or b.ceo like concat('%',#{searchKeyword},'%') )
and a.title like concat('%',#{searchKeyword},'%')
and a.content like concat('%',#{searchKeyword},'%')
and b.ceo like concat('%',#{searchKeyword},'%')
order by no desc LIMIT #{recordCountPerPage} OFFSET #{firstIndex}

select count(*) tocnt from tb_inquiry a inner join tb_store b on a.store_id = b.store_id where (SELECT @ROWNUM:=0)=0 and a.role_id = #{role_id}
and a.store_id = #{store_id}
and (a.title like concat('%',#{searchKeyword},'%') or a.content like concat('%',#{searchKeyword},'%') or b.ceo like concat('%',#{searchKeyword},'%') )
and a.title like concat('%',#{searchKeyword},'%')
and a.content like concat('%',#{searchKeyword},'%')
and b.ceo like concat('%',#{searchKeyword},'%')

select @ROWNUM:=@ROWNUM+1 AS rownum, a.no, a.store_id, a.role_id, a.cnt, a.title, a.tel, a.email, a.role_check, b.ceo, date_format(a.created_datetime, '%Y.%m.%d') created_datetime from tb_pay_cancel_req a inner join tb_store b on a.store_id = b.store_id where (SELECT @ROWNUM:=0)=0
and a.role_id = #{role_id}
and a.store_id = #{store_id}
and (a.title like concat('%',#{searchKeyword},'%') or a.content like concat('%',#{searchKeyword},'%') or b.ceo like concat('%',#{searchKeyword},'%') )
and a.title like concat('%',#{searchKeyword},'%')
and a.content like concat('%',#{searchKeyword},'%')
and b.ceo like concat('%',#{searchKeyword},'%')
order by no desc LIMIT #{recordCountPerPage} OFFSET #{firstIndex}
