mapper.select:
/** selectLogList */ select a.rownum, a.no, a.login_id, a.ip, a.action, a.url, a.login, a.created_datetime, a.updated_datetime from ( select @ROWNUM:=@ROWNUM+1 AS rownum, a.no, a.login_id, a.ip, a.action, a.url, a.login, a.created_datetime, a.updated_datetime from ( select no, login_id, ip, action, url, login, date_format(created_datetime, '%Y.%m.%d %h:%i:%s') created_datetime, date_format(updated_datetime, '%Y.%m.%d %h:%i:%s') updated_datetime from tb_log where 1 = 1
and login_id like concat('%', #{searchKeyword}, '%')
and ip like concat('%', #{searchKeyword}, '%')
and url like concat('%', #{searchKeyword}, '%')
and action like concat('%', #{searchKeyword}, '%')
and login like concat('%', #{searchKeyword}, '%')
and date(created_datetime) BETWEEN #{start_datetime} AND #{end_datetime}
)a where (SELECT @ROWNUM:=0)=0 order by no desc LIMIT #{recordCountPerPage} OFFSET #{firstIndex} )a

mapper.delete:
	/** logDel */
	   delete from tb_log
	    where 1 = 1

          and no in 

             #{item}
